import React, { useState, useContext } from 'react';
import Context from '../index';
import Nsearch from '../UI/Nsearch/Nsearch';
import Burger from '../UI/Burger/Burger';
import AddButton from '../UI/AddButton/AddButton';
import NoteViewer from './NoteViewer';
import Sidebar from './Sidebar';
function Header({SearchSync, search, setSearch }) {
    const {store} = useContext(Context);
    const [noteIsEdit, setNoteIsEdit] = useState(false);
    const [sidemenu, setSidemenu] = useState(false);
    const callback = (e) => {
        setNoteIsEdit(false);
        store.AddNote(e);
    }
    return (
        <div className='container'>
            <div className="header">
                <Burger onClick={()=>{setSidemenu(true)}}></Burger>
                <Sidebar isActive={sidemenu} setActive={setSidemenu} onClick={()=>{setSidemenu(false)}} ></Sidebar>
                <Nsearch isSync={SearchSync} search={search} setSearch={setSearch}></Nsearch>
                <AddButton onClick={() => { setNoteIsEdit(true); }}></AddButton>
                <NoteViewer note={{title: "", body: ""}} isActive={noteIsEdit} setActive={setNoteIsEdit} callback={callback} removeCallback={()=>{noteIsEdit(false)}}></NoteViewer>
            </div>
        </div>
    )
}

export default Header;
