import React from 'react';
import styles from './NoteViewer.module.css'
import ContentEditable from 'react-contenteditable'
import Modal from '../UI/Modal/Modal';
import Back from '../UI/Buttons/Back/Back';
import Remove from '../UI/Buttons/Remove/Remove';
function NoteViewer({ note, isActive, setActive, callback, removeCallback }) {
    let editingNote = note //эта переменная нужна для временного изменения. финальная запись будет при закрытии.
    return (
        <Modal isActive={isActive} onClick={() => { callback(editingNote) }}>
            <div className={styles.viewer}>
                <ContentEditable
                    placeholder="Enter Title here..."
                    spellCheck={false}
                    className={styles.viewer__title}
                    html={editingNote.title} // innerHTML of the editable div
                    onChange={evt => { editingNote = { ...editingNote, title: evt.target.value }; }} // handle innerHTML change
                />
                <ContentEditable
                    placeholder="Enter Text here..."
                    spellCheck={false}
                    className={styles.viewer__body}
                    html={editingNote.body} // innerHTML of the editable div
                    onChange={evt => { editingNote = { ...editingNote, body: evt.target.value }; }} // handle innerHTML change
                />
                <div className={styles.viewer__bar}>
                    <Back onClick={() => { setActive(false); callback(editingNote) }} />
                    <Remove onClick={() => { setActive(false); removeCallback(editingNote); }}></Remove>
                </div>
            </div>
        </Modal>
    )
}

export default NoteViewer;
