import { makeAutoObservable } from 'mobx';
import AuthService from '../services/auth-service';
import noteService from '../services/note-service';
import localdb from '../local_db/localdb';
export default class Store {
    //auth data
    user = {};
    isAuth = false;

    //notes data
    notes = [];
    searchedNotes = [];

    constructor() {
        makeAutoObservable(this);
    }

    setAuth(auth) {
        this.isAuth = auth;
    }
    setUser(user) {
        this.user = user;
    }

    setNotes(notes) {
        this.notes = notes;
    }
    setSearchedNotes(notes) {
        this.searchedNotes = notes;
    }

    async login(email, password) {
        try {
            const response = await AuthService.login(email, password);
            console.log(response);
            localStorage.setItem('token', response.accessToken);
            this.setAuth(true);
            this.setUser(response.user)
            this.getNotes();

        } catch (error) {
            return error.response.data;
        }
    }

    async registration(email, password, name) {
        try {
            const response = await AuthService.registration(email, password, name);
            console.log(response);
            localStorage.setItem('token', response.accessToken);
            this.setAuth(true);
            this.setUser(response.user);
        } catch (error) {
            return error.response.data;
        }
    }
    async logout() {
        try {
            console.log('logout')
            await AuthService.logout();
            localStorage.removeItem('token')
            this.setAuth(false);
            this.setUser({});
        } catch (error) {
            console.log(error);
        }
    }
    async checkAuth() {
        try {
            const response = await AuthService.checkAuth();
            localStorage.setItem('token', response.accessToken)
            this.setAuth(true);
            this.setUser(response.user)
            this.getNotes();
        } catch (error) {

        }
    }

    async sendResetPasswordEmail(email) {
        try {
            const response = await AuthService.sendResetPasswordEmail(email);
            console.log(response)
        } catch (error) {
            return error.response.data;
        }
    }

    //notes
    async getNotes() {
        const LocalNotes = await localdb.getAll()
        this.setNotes(LocalNotes);
        /*noteService.get().then(res => { //если сервер доступен удаляем заметку из notes и меняем ее на валидную
            if (res.status == 200) {
                res.data.forEach(async serverNote => { //проходимся по каждой заметки из сервера и проверяем по _id есть ли она в локальной бд
                    const isSynced = LocalNotes.find(element => element._id == serverNote._id)
                    if(isSynced == undefined) await localdb.add({...serverNote, status: 'sync'})
                });

            }
        })*/
  
    }
    async editNote(note, sync) {
        try {
            //await service.noteChange(editedNote)
            await noteService.edit(note)
            let newArray = [];
            for (let i in this.notes) {
                if (this.notes[i]._id === note._id) {
                    newArray.push(note)
                }
                else {
                    newArray.push(this.notes[i])
                }
            }
            this.setNotes(newArray);

        } catch (error) {
            console.log(error)
        }
        finally {
        }


    }
    async AddNote(note, sync) {
        try {
            // setIsSync(true)
            const localNote = { ...note, status: 'new', _id: new Date() } //сначала создаем заметку в бд
            await localdb.add(localNote)
            this.setNotes([...this.notes, localNote])
            noteService.add(note).then(res => { //если сервер доступен удаляем заметку из notes и меняем ее на валидную
                if (res.status == 200) {
                    const syncNote = res.data;
                    const notes = this.notes.filter(e => e._id !== localNote._id)
                    localdb.delete(localNote._id)
                    localdb.add({...syncNote, status: 'sync'})
                    this.setNotes([...notes, syncNote])
                }
            })

        } catch (error) {
            console.log(error);
        }
        finally {
            //setIsSync(false)
        }

    }
    async RemoveNote(note, sync) {
        try {
            await noteService.delete(note)
            const notes = this.notes.filter(e => e._id !== note._id)
            this.setNotes(notes)
        } catch (error) {

        }
    }
    NoteSearch(search) {
        const notes = this.notes.filter(note => note.title.toLowerCase().includes(search.toLowerCase()))
            .concat(this.notes.filter(note => note.body.toLowerCase().includes(search.toLowerCase())))
        this.setSearchedNotes(notes);
    }
}
