import React,{useState} from 'react';
import styles from './NoteViewer.module.css'
import ContentEditable from 'react-contenteditable'
import Modal from '../UI/Modal/Modal';
import Back from '../UI/Buttons/Back/Back';
import Remove from '../UI/Buttons/Remove/Remove';
function NoteViewer({ note, isActive, setActive, callback, removeCallback}) {
    const [editingNote,setEditingNote] = useState(note); //эта переменная нужна для временного изменения. финальная запись будет при закрытии.

    return (
        <Modal isActive={isActive} onClick={()=>{callback(editingNote)}}>
            <div className={styles.viewer}>
                <ContentEditable
                    spellCheck={false}
                    className={styles.viewer__title}
                    html={editingNote.title} // innerHTML of the editable div
                    onChange={evt => { setEditingNote({...editingNote, title: evt.target.value});}} // handle innerHTML change
                    onFocus={()=>{ //при фокусировке убрать плейсхолдер
                        if(editingNote.title == 'Title...' && editingNote?._id == undefined){//если это новая заметка удаляет плейсхолдер, если заметка старая позволяет оставить текст плейсхолдера
                            setEditingNote({...editingNote, title: ''});
                        }
                    }}
                />
                <ContentEditable
                    spellCheck={false}
                    className={styles.viewer__body}
                    html={editingNote.body} // innerHTML of the editable div
                    onChange={evt => { setEditingNote({...editingNote, body: evt.target.value});}} // handle innerHTML change
                    onFocus={()=>{ //при фокусировке убрать плейсхолдер
                        if(editingNote.title == 'Write here...' && editingNote?._id == undefined){//если это новая заметка удаляет плейсхолдер, если заметка старая позволяет оставить текст плейсхолдера
                            setEditingNote({...editingNote, body: ''});
                        }
                    }}
                />
                <div className={styles.viewer__bar}>
                    <Back onClick={()=>{setActive(false);callback(editingNote)}}/>
                    <Remove onClick={()=>{setActive(false);removeCallback(editingNote);}}></Remove>
                </div>
            </div>
        </Modal>
    )
}

export default NoteViewer;
