import React, { useState, useEffect, useMemo, useContext } from 'react';
import { observer } from 'mobx-react-lite';
import Context from './index';
import Header from './components/Header';
import NoteList from './components/NoteList';
import Loading from './UI/Loading/Loading';
import localdb from './local_db/localdb';
import { openDB } from 'idb';

function App() {
    const { store } = useContext(Context)
    const [search, setSearch] = useState('');
    const [isSync, setIsSync] = useState(true);
    const [isLoading, setIsLoading] = useState(true);
    useEffect( () => {
        let openRequest = indexedDB.open("notes", 1);

        // создаём хранилище объектов для books, если ешё не существует
        openRequest.onupgradeneeded = function() {
          let db = openRequest.result;
          if (!db.objectStoreNames.contains('notes')) { // если хранилище "books" не существует
            db.createObjectStore('notes', {keyPath: '_id'}); // создаём хранилище
          }
        };
        try {
            if (localStorage.getItem('token')) {
                store.checkAuth();
            }

           


        } catch (error) {
            console.log(error)
        }
        finally {
            setIsLoading(false)
        }
    }, [])
    useMemo(() => {
        store.NoteSearch(search)
    }, [search, store.notes])
    return (
        <div>
            <Header SearchSync={isSync} search={search} setSearch={setSearch}></Header>
            <NoteList></NoteList>
            {isLoading &&
                <div style={{ display: 'flex', justifyContent: 'center' }}><Loading /></div>
            }
        </div>
    )
}

export default observer(App);
